      return { id: 1, name: "John Smith 2", status: "" };
    },
    git_user: async (_parent, _args, _context, _info) => {
      console.log("_args => ", _args);
      let github_user = _args.id;
      try {
        return fetch(`https://api.github.com/users/${github_user}`)
