// Hold all endpoints data here
import fetch from "node-fetch";
const GITHUB_BASE_API = "https://api.github.com";

export const resolvers = {
  Query: {
    viewer(_parent, _args, _context, _info) {
      return { id: 1, name: "John Smith 2", status: "" };
    },
    git_user: async (parent, args, context, info) => {
      console.log("git_user args => ", args.id);
      // let github_user = _args.id;
      let { id } = args;

      return fetch(`${GITHUB_BASE_API}/users/${{ id }}`)
        .then(res => res.json())
        .then(git_data => {
          console.log("git_data => ", git_data);
          if (git_data.id) {
            return {
              id: git_data.id,
              name: `Mr Git ${git_data.login}`,
              status: "wip"
            };
          } else {
            return {
              id: 404,
              name: git_data.message,
              status: git_data.message
            };
          }
        })
        .catch(err => {
          return {
            id: 404,
            name: err.message,
            status: err.message
          };
        });
    }
  }
};
